import React, { useRef, useEffect, useCallback, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Hands } from '@mediapipe/hands';
import { Camera } from '@mediapipe/camera_utils';
import * as drawingUtils from '@mediapipe/drawing_utils';
import '../styles/Home.css';

function Home() {
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [gesture, setGesture] = useState('손 모양을 감지 중...');
  const [showPopup, setShowPopup] = useState(false);

  const startOrder = useCallback(() => {
    console.log('버튼이 클릭되었습니다.');
    navigate('/menu-selection');
  }, [navigate]);

  useEffect(() => {
    const hands = new Hands({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`,
    });

    hands.setOptions({
      maxNumHands: 1,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    hands.onResults((results) => {
      const canvasCtx = canvasRef.current.getContext('2d');
      canvasCtx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      canvasCtx.drawImage(results.image, 0, 0, canvasRef.current.width, canvasRef.current.height);
      
      if (results.multiHandLandmarks && results.multiHandLandmarks.length > 0) {
        for (const landmarks of results.multiHandLandmarks) {
          drawingUtils.drawConnectors(canvasCtx, landmarks, Hands.HAND_CONNECTIONS, { color: '#00FF00', lineWidth: 5 });
          drawingUtils.drawLandmarks(canvasCtx, landmarks, { color: '#FF0000', lineWidth: 2 });
        }
        setGesture('손을 감지했습니다!');
      } else {
        setGesture('손 모양을 감지 중...');
      }
    });

    if (videoRef.current) {
      const camera = new Camera(videoRef.current, {
        onFrame: async () => {
          await hands.send({ image: videoRef.current });
        },
        width: 1280,
        height: 720,
      });
      camera.start();
    }

    return () => {
      hands.close();
    };
  }, []);

  const handlePopupConfirm = () => {
    setShowPopup(false);
    startOrder();
  };

  const handlePopupCancel = () => {
    setShowPopup(false);
  };

  return (
    <div className="home-container">
      <h1>Welcome to the Cafe</h1>
      <button 
        onClick={() => {
          console.log('버튼 클릭 이벤트 발생');
          startOrder();
        }} 
        className="start-button"
      >
        주문하기
      </button>
      <video ref={videoRef} className="video-feed" autoPlay playsInline muted width="640" height="480"></video>
      <canvas ref={canvasRef} width="640" height="480" style={{ display: 'none' }}></canvas>
      <p>인식된 제스처: {gesture}</p>

      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <p>good 제스처를 취하셨습니다. 맞나요?</p>
            <button onClick={handlePopupConfirm}>예</button>
            <button onClick={handlePopupCancel}>아니오</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
