import React, { useEffect, useRef, useState } from 'react';
import { Hands } from '@mediapipe/hands';
import { Camera } from '@mediapipe/camera_utils';
import * as drawingUtils from '@mediapipe/drawing_utils'; // drawingUtils 모듈 임포트
import '../styles/WebcamCapture.css';

const WebcamCapture = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [action, setAction] = useState('손 모양을 감지 중...');

  useEffect(() => {
    const hands = new Hands({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/hands/${file}`,
    });

    hands.setOptions({
      maxNumHands: 1,
      minDetectionConfidence: 0.5,
      minTrackingConfidence: 0.5,
    });

    hands.onResults((results) => {
      const canvasCtx = canvasRef.current.getContext('2d');
      canvasCtx.save();
      canvasCtx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      canvasCtx.drawImage(
        results.image, 0, 0, canvasRef.current.width, canvasRef.current.height);
      if (results.multiHandLandmarks) {
        for (const landmarks of results.multiHandLandmarks) {
          // 손 모양의 관절을 그리는 함수 호출
          drawingUtils.drawLandmarks(canvasCtx, landmarks, Hands.HAND_CONNECTIONS, // 관절 그리기
            { color: '#00FF00', lineWidth: 5 }); // 관절 스타일 설정
          setAction('손을 감지했습니다!');
        }
      } else {
        setAction('손 모양을 감지 중...');
      }
      canvasCtx.restore();
    });

    if (videoRef.current) {
      const camera = new Camera(videoRef.current, {
        onFrame: async () => {
          await hands.send({ image: videoRef.current });
        },
        width: 1280,
        height: 720,
      });
      camera.start();
    }

    return () => {
      hands.close();
    };
  }, []);

  return (
    <div className="webcam-capture-container">
      <video ref={videoRef} className="webcam-video" autoPlay playsInline muted />
      <canvas ref={canvasRef} className="webcam-canvas" />
      <div className="action-display">{action}</div>
    </div>
  );
};

export default WebcamCapture;
